package one.block.eosiojava.models;

import java.util.ArrayList;
import java.util.List;
import one.block.eosiojava.models.rpcProvider.ContextFreeData;
import org.junit.Before;
import org.junit.Test;
import static junit.framework.TestCase.*;

public class ContextFreeDataTest {
    ContextFreeData contextFreeData;

    public void setup(List<String> cfd) {
        contextFreeData = new ContextFreeData(cfd);
    }

    @Before
    public void setup() {
        this.setup(new ArrayList<String>());
    }

    //region Default Data
    @Test
    public void testCreateContextFreeDataWithEmptyData() {
        assertFalse(contextFreeData.hasData());
        assertTrue(contextFreeData.getData().isEmpty());
        assertEquals(contextFreeData.getBytes().length, 0);
        assertEquals(contextFreeData.getHexed(), "");
        assertEquals(contextFreeData.getSerialized(), "");
    }

    @Test
    public void hasDataReturnsTrueWhenDataSetup() {
        setup(this.defaultContextFreeData());

        assertTrue(contextFreeData.hasData());
    }

    @Test
    public void testGetDataReturnsRawContextFreeData() {
        setup(this.defaultContextFreeData());

        assertEquals(contextFreeData.getData(), this.defaultContextFreeData());
    }

    @Test
    public void testGetBytesReturnsFormattedBytes() {
        setup(this.defaultContextFreeData());

        assertEquals(contextFreeData.getBytes().length, byteLengthDefaultDataset);
    }

    @Test
    public void testGetHexContextFreeDataReturnsCorrectlyFormattedHexContextFreeData() {
        setup(this.defaultContextFreeData());

        assertEquals(contextFreeData.getHexed(), hexedDefaultDataset);
    }

    @Test
    public void testGetSerializedContextFreeDataReturnsCorrectlyFormattedSerializedContextFreeData() {
        setup(this.defaultContextFreeData());

        assertEquals(contextFreeData.getSerialized(), serializedDefaultDataset);
    }

    private List<String> defaultContextFreeData() {
        String contextFreeData1 = smallCharString;
        String contextFreeData2 = "{\"some\": \"jsonData\"}";
        String contextFreeData3 = "!@#$%^&*()_+";
        String contextFreeData4 = "This is some long context free data input. It can have whatever data you want in it. It will be copied multiple times to increase length. This is some long context free data input. It can have whatever data you want in it. It will be copied multiple times to increase length. This is some long context free data input. It can have whatever data you want in it. It will be copied multiple times to increase length.This is some long context free data input. It can have whatever data you want in it. It will be copied multiple times to increase length. This is some long context free data input. It can have whatever data you want in it. It will be copied multiple times to increase length. This is some long context free data input. It can have whatever data you want in it. It will be copied multiple times to increase length.This is some long context free data input. It can have whatever data you want in it. It will be copied multiple times to increase length. This is some long context free data input. It can have whatever data you want in it. It will be copied multiple times to increase length. This is some long context free data input. It can have whatever data you want in it. It will be copied multiple times to increase length.";

        ArrayList<String> contextFreeData = new ArrayList<String>();
        contextFreeData.add(contextFreeData1);
        contextFreeData.add(contextFreeData2);
        contextFreeData.add(contextFreeData3);
        contextFreeData.add(contextFreeData4);

        return contextFreeData;
    }

    private static final int byteLengthDefaultDataset = 1281;
    private static final String hexedDefaultDataset = "";
    private static final String serializedDefaultDataset = "6595140530fcbd94469196e5e6d73af65693910df8fcf5d3088c3616bff5ee9f";
    //endregion

    //region Large Dataset
    @Test
    public void testLargeDatasetFormatsProperly() {
        int count = 128;
        setup(this.largeContextFreeDataset(count));

        assertEquals(contextFreeData.getData().size(), count);
        assertEquals(contextFreeData.getBytes().length, byteLengthLargeDataset);
        assertEquals(contextFreeData.getHexed(), hexedLargeDataset);
        assertEquals(contextFreeData.getSerialized(), serializedLargeDataset);
    }

    @Test
    public void testLargeDatasetWithLargeDataFormatsProperly() {
        int count = 128;
        setup(this.largeContextFreeDataset(count, true));

        assertEquals(contextFreeData.getData().size(), count);
        assertEquals(contextFreeData.getBytes().length, byteLengthLargeDatasetWithLargeData);
        assertEquals(contextFreeData.getHexed(), hexedLargeDatasetWithLargeData);
        assertEquals(contextFreeData.getSerialized(), serializedLargeDatasetWithLargeData);
    }

    private List<String> largeContextFreeDataset(int count) {
        return this.largeContextFreeDataset(count, false);
    }

    private List<String> largeContextFreeDataset(int count, boolean largeDataSize) {
        List<String> contextFreeData = new ArrayList<String>();
        for(int i = 0; i < count; i++) {
            if (largeDataSize) {
                contextFreeData.add(large128CharString + i);
            } else {
                contextFreeData.add(smallCharString + i);
            }
        }

        return contextFreeData;
    }

    private static final int byteLengthLargeDataset = 916;
    private static final int byteLengthLargeDatasetWithLargeData = 16916;
    private static final String hexedLargeDataset = "";
    private static final String hexedLargeDatasetWithLargeData = "";
    private static final String serializedLargeDataset = "4b36abb399e12b53d97fda8e7902d81bdd64e5bf4b986503d8e49941285f4413";
    private static final String serializedLargeDatasetWithLargeData = "3914ed979241236bbeb9f97d5bb1ccd40ed525ed62bb2d613fe2b44221aa22e1";
    private static final String smallCharString = "test";
    private static final String large128CharString = "testtesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttesttest";
    //endregion
}
